




-- check điều kiện 15 user, có 5 user thuộc 2 phòng ban khác nhau, 3 user có 2 role trong 1 phòng.
SELECT u.id, r.`name`, d.`name`
FROM users u
JOIN user_department ud
ON u.id = ud.user_id
JOIN departments d
ON ud.department_id = d.id
JOIN user_role ur
ON ur.user_department_id = ud.id
JOIN roles r
ON ur.role_id = r.id;






